import React, { useState, useEffect } from 'react';
import { RefreshCw, Filter, TrendingUp, Package, DollarSign, Weight } from 'lucide-react';

const SCRIPT_URL = 'http://localhost:4000/api/products';

export interface Product {
    id: number;
    pricePerKg: number;
    totalCost: number;
    weight: number;
}

export default function GoodsApp() {
    const [goods, setGoods] = useState<Product>([]);
    const [stats, setStats] = useState<string|null>(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [lastUpdate, setLastUpdate] = useState('');

    const [filters, setFilters] = useState({
        minPrice: '',
        maxPrice: '',
        minWeight: '',
        maxWeight: ''
    });

    useEffect(() => {
        loadGoods();
        loadStats();

        const interval = setInterval(() => {
            loadGoods();
            loadStats();
        }, 30000);

        return () => clearInterval(interval);
    }, []);

    const loadGoods = async (customFilters = filters) => {
        setLoading(true);
        setError('');

        try {
            let url = `${SCRIPT_URL}?action=getAll`;
            if (customFilters.minPrice) url += `&minPrice=${customFilters.minPrice}`;
            if (customFilters.maxPrice) url += `&maxPrice=${customFilters.maxPrice}`;
            if (customFilters.minWeight) url += `&minWeight=${customFilters.minWeight}`;
            if (customFilters.maxWeight) url += `&maxWeight=${customFilters.maxWeight}`;

            const response = await fetch(url);
            const data = await response.json();

            if (data.success) {
                setGoods(data.goods || []);
                if (data.timestamp) {
                    setLastUpdate(new Date(data.timestamp).toLocaleString('ru-RU'));
                }
            } else {
                throw new Error(data.error || 'Ошибка загрузки данных');
            }
        } catch {
            setError(`Ошибка загрузки`);
            setGoods([]);
        } finally {
            setLoading(false);
        }
    };

    const loadStats = async () => {
        try {
            const response = await fetch(`${SCRIPT_URL}?action=getStats`);
            const data = await response.json();

            if (data.success && data.stats) {
                setStats(data.stats);
            }
        } catch (err) {
            console.error('Ошибка загрузки статистики:', err);
        }
    };

    const applyFilters = () => {
        loadGoods(filters);
    };

    const resetFilters = () => {
        const emptyFilters = {
            minPrice: '',
            maxPrice: '',
            minWeight: '',
            maxWeight: ''
        };
        setFilters(emptyFilters);
        loadGoods(emptyFilters);
    };

    const handleFilterChange = (field, value) => {
        setFilters(prev => ({ ...prev, [field]: value }));
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4 md:p-8">
            <div className="max-w-7xl mx-auto">
                <div className="bg-white rounded-2xl shadow-xl p-6 md:p-8 mb-6">
                    <div className="flex items-center justify-between flex-wrap gap-4">
                        <div>
                            <h1 className="text-3xl md:text-4xl font-bold text-indigo-600 mb-2">
                                📊 Учёт товаров
                            </h1>
                            <p className="text-gray-600">Данные из Google Sheets в реальном времени</p>
                            <p className="text-sm text-gray-500 mt-1">Автообновление каждые 30 секунд</p>
                        </div>
                        <button
                            onClick={() => { loadGoods(); loadStats(); }}
                            disabled={loading}
                            className="flex items-center gap-2 px-6 py-3 bg-indigo-600 text-white rounded-xl hover:bg-indigo-700 transition-all disabled:opacity-50 disabled:cursor-not-allowed shadow-lg hover:shadow-xl"
                        >
                            <RefreshCw className={`w-5 h-5 ${loading ? 'animate-spin' : ''}`} />
                            Обновить
                        </button>
                    </div>
                </div>

                {stats && (
                    <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
                        <div className="bg-gradient-to-br from-blue-500 to-blue-600 rounded-xl shadow-lg p-6 text-white">
                            <div className="flex items-center justify-between mb-2">
                                <Package className="w-8 h-8 opacity-80" />
                                <TrendingUp className="w-5 h-5 opacity-60" />
                            </div>
                            <div className="text-3xl font-bold mb-1">{stats.totalItems}</div>
                            <div className="text-blue-100 text-sm">Всего товаров</div>
                        </div>

                        <div className="bg-gradient-to-br from-green-500 to-green-600 rounded-xl shadow-lg p-6 text-white">
                            <div className="flex items-center justify-between mb-2">
                                <Weight className="w-8 h-8 opacity-80" />
                            </div>
                            <div className="text-3xl font-bold mb-1">{stats.totalWeight} кг</div>
                            <div className="text-green-100 text-sm">Общий вес</div>
                        </div>

                        <div className="bg-gradient-to-br from-purple-500 to-purple-600 rounded-xl shadow-lg p-6 text-white">
                            <div className="flex items-center justify-between mb-2">
                                <DollarSign className="w-8 h-8 opacity-80" />
                            </div>
                            <div className="text-3xl font-bold mb-1">{stats.totalCost.toLocaleString()} ₸</div>
                            <div className="text-purple-100 text-sm">Общая стоимость</div>
                        </div>

                        <div className="bg-gradient-to-br from-orange-500 to-orange-600 rounded-xl shadow-lg p-6 text-white">
                            <div className="flex items-center justify-between mb-2">
                                <TrendingUp className="w-8 h-8 opacity-80" />
                            </div>
                            <div className="text-3xl font-bold mb-1">{stats.avgPrice.toLocaleString()} ₸</div>
                            <div className="text-orange-100 text-sm">Средняя цена/кг</div>
                        </div>
                    </div>
                )}

                <div className="bg-white rounded-2xl shadow-xl p-6 mb-6">
                    <div className="flex items-center gap-2 mb-4">
                        <Filter className="w-5 h-5 text-indigo-600" />
                        <h2 className="text-xl font-bold text-gray-800">Фильтры</h2>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Мин. цена (₸/кг)
                            </label>
                            <input
                                type="number"
                                value={filters.minPrice}
                                onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                                placeholder="от 0"
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                            />
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Макс. цена (₸/кг)
                            </label>
                            <input
                                type="number"
                                value={filters.maxPrice}
                                onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                                placeholder="до ∞"
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                            />
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Мин. вес (кг)
                            </label>
                            <input
                                type="number"
                                value={filters.minWeight}
                                onChange={(e) => handleFilterChange('minWeight', e.target.value)}
                                placeholder="от 0"
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                            />
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Макс. вес (кг)
                            </label>
                            <input
                                type="number"
                                value={filters.maxWeight}
                                onChange={(e) => handleFilterChange('maxWeight', e.target.value)}
                                placeholder="до ∞"
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                            />
                        </div>
                    </div>

                    <div className="flex gap-3">
                        <button
                            onClick={applyFilters}
                            className="flex-1 px-6 py-3 bg-indigo-600 text-white rounded-xl hover:bg-indigo-700 transition-all shadow-md hover:shadow-lg font-semibold"
                        >
                            Применить фильтры
                        </button>
                        <button
                            onClick={resetFilters}
                            className="px-6 py-3 bg-gray-200 text-gray-700 rounded-xl hover:bg-gray-300 transition-all font-semibold"
                        >
                            Сбросить
                        </button>
                    </div>
                </div>

                <div className="bg-white rounded-xl shadow-md p-4 mb-6 flex justify-between items-center">
                    <div className="text-sm text-gray-600">
                        <span className="font-semibold">Найдено товаров:</span> {goods.length} |
                        <span className="font-semibold ml-3">Обновлено:</span> {lastUpdate || '—'}
                    </div>
                    {loading && (
                        <span className="text-indigo-600 font-semibold animate-pulse">
              ⏳ Загрузка...
            </span>
                    )}
                </div>

                {error && (
                    <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-lg mb-6">
                        ❌ {error}
                    </div>
                )}

                <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
                    <div className="overflow-x-auto">
                        <table className="w-full">
                            <thead className="bg-indigo-600 text-white">
                            <tr>
                                <th className="px-6 py-4 text-left font-semibold">ID</th>
                                <th className="px-6 py-4 text-left font-semibold">Вес (кг)</th>
                                <th className="px-6 py-4 text-left font-semibold">Цена за 1кг (₸)</th>
                                <th className="px-6 py-4 text-left font-semibold">Стоимость (₸)</th>
                            </tr>
                            </thead>
                            <tbody>
                            {goods.length === 0 ? (
                                <tr>
                                    <td colSpan={4} className="px-6 py-12 text-center text-gray-500">
                                        {loading ? '⏳ Загрузка данных...' : '😔 Товары не найдены'}
                                    </td>
                                </tr>
                            ) : (
                                goods.map((item, index) => (
                                    <tr
                                        key={item.id}
                                        className={`border-b hover:bg-indigo-50 transition-colors ${
                                            index % 2 === 0 ? 'bg-gray-50' : 'bg-white'
                                        }`}
                                    >
                                        <td className="px-6 py-4">
                        <span className="inline-block bg-indigo-100 text-indigo-800 px-3 py-1 rounded-full font-semibold">
                          #{item.id}
                        </span>
                                        </td>
                                        <td className="px-6 py-4 font-semibold text-gray-700">
                                            {item.weight.toFixed(1)} кг
                                        </td>
                                        <td className="px-6 py-4 text-gray-700">
                                            {item.pricePerKg.toLocaleString()} ₸
                                        </td>
                                        <td className="px-6 py-4">
                        <span className="text-lg font-bold text-indigo-600">
                          {item.totalCost.toLocaleString()} ₸
                        </span>
                                        </td>
                                    </tr>
                                ))
                            )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
}
