// libraries
import { useState, useEffect } from 'react';
// icons
import { RefreshCw, Filter, TrendingUp, Package, DollarSign, Weight } from 'lucide-react';
// config
import {SCRIPT_URL} from "./config";
// style
import '../../style/components/googleApp.css';


const GoodsApp = () => {
    const [goods, setGoods] = useState<Good[]>([]);
    const [stats, setStats] = useState<Stats | null>(null);
    const [loading, setLoading] = useState<boolean>(false);
    const [error, setError] = useState<string>('');
    const [lastUpdate, setLastUpdate] = useState<string>('');

    const [filters, setFilters] = useState<Filters>({
        minPrice: '',
        maxPrice: '',
        minWeight: '',
        maxWeight: ''
    });

    useEffect(() => {
        loadGoods();
        loadStats();

        const interval = setInterval(() => {
            loadGoods();
            loadStats();
        }, 30000);

        return () => clearInterval(interval);
    }, []);

    const loadGoods = async (customFilters: Filters = filters): Promise<void> => {
        setLoading(true);
        setError('');

        try {

        } catch (err) {
            const msg = err instanceof Error ? err.message : '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞';
            setError(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏: ${msg}`);
            setGoods([]);
        } finally {
            setLoading(false);
        }
    };

    const loadStats = async (): Promise<void> => {
        try {
            const response = await fetch(`${SCRIPT_URL}?action=getStats`);
            const data: ApiResponse = await response.json();
            if (data.success && data.stats) setStats(data.stats);
        } catch (err) {
            console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∏:', err);
        }
    };

    const handleFilterChange = (field: keyof Filters, value: string): void => {
        setFilters(prev => ({ ...prev, [field]: value }));
    };

    return (
        <div className="goods-app">
            <div className="header-card">
                <div>
                    <h1>üìä –£—á—ë—Ç —Ç–æ–≤–∞—Ä–æ–≤</h1>
                    <p>–î–∞–Ω–Ω—ã–µ –∏–∑ Google Sheets –≤ —Ä–µ–∞–ª—å–Ω–æ–º –≤—Ä–µ–º–µ–Ω–∏</p>
                    <small>–ê–≤—Ç–æ–æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –∫–∞–∂–¥—ã–µ 30 —Å–µ–∫—É–Ω–¥</small>
                </div>
                <button
                    onClick={() => { loadGoods(); loadStats(); }}
                    disabled={loading}
                    className="refresh-btn"
                >
                    <RefreshCw className={loading ? 'spin' : ''} /> –û–±–Ω–æ–≤–∏—Ç—å
                </button>
            </div>

            {stats && (
                <div className="stats-grid">
                    <div className="stat blue">
                        <Package /> <TrendingUp />
                        <div className="stat-value">{stats.totalItems}</div>
                        <div className="stat-label">–í—Å–µ–≥–æ —Ç–æ–≤–∞—Ä–æ–≤</div>
                    </div>
                    <div className="stat green">
                        <Weight />
                        <div className="stat-value">{stats.totalWeight} –∫–≥</div>
                        <div className="stat-label">–û–±—â–∏–π –≤–µ—Å</div>
                    </div>
                    <div className="stat purple">
                        <DollarSign />
                        <div className="stat-value">{stats.totalCost.toLocaleString()} ‚Ç∏</div>
                        <div className="stat-label">–û–±—â–∞—è —Å—Ç–æ–∏–º–æ—Å—Ç—å</div>
                    </div>
                    <div className="stat orange">
                        <TrendingUp />
                        <div className="stat-value">{stats.avgPrice.toLocaleString()} ‚Ç∏</div>
                        <div className="stat-label">–°—Ä–µ–¥–Ω—è—è —Ü–µ–Ω–∞/–∫–≥</div>
                    </div>
                </div>
            )}

            <div className="filters-card">
                <div className="filters-header"><Filter /> –§–∏–ª—å—Ç—Ä—ã</div>
                <div className="filters-grid">
                    <input type="number" placeholder="–ú–∏–Ω. —Ü–µ–Ω–∞" value={filters.minPrice}
                           onChange={e => handleFilterChange('minPrice', e.target.value)} />
                    <input type="number" placeholder="–ú–∞–∫—Å. —Ü–µ–Ω–∞" value={filters.maxPrice}
                           onChange={e => handleFilterChange('maxPrice', e.target.value)} />
                    <input type="number" placeholder="–ú–∏–Ω. –≤–µ—Å" value={filters.minWeight}
                           onChange={e => handleFilterChange('minWeight', e.target.value)} />
                    <input type="number" placeholder="–ú–∞–∫—Å. –≤–µ—Å" value={filters.maxWeight}
                           onChange={e => handleFilterChange('maxWeight', e.target.value)} />
                </div>
                <div className="filters-actions">
                    <button onClick={() => loadGoods(filters)} className="apply-btn">–ü—Ä–∏–º–µ–Ω–∏—Ç—å</button>
                    <button onClick={() => setFilters({ minPrice: '', maxPrice: '', minWeight: '', maxWeight: '' })}
                            className="reset-btn">–°–±—Ä–æ—Å–∏—Ç—å</button>
                </div>
            </div>

            <div className="table-info">
                <span>–ù–∞–π–¥–µ–Ω–æ —Ç–æ–≤–∞—Ä–æ–≤: {goods.length}</span> | <span>–û–±–Ω–æ–≤–ª–µ–Ω–æ: {lastUpdate || '‚Äî'}</span>
                {loading && <span className="loading">‚è≥ –ó–∞–≥—Ä—É–∑–∫–∞...</span>}
            </div>

            {error && <div className="error-box">‚ùå {error}</div>}

            <div className="table-wrapper">
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>–í–µ—Å (–∫–≥)</th>
                        <th>–¶–µ–Ω–∞ –∑–∞ 1–∫–≥ (‚Ç∏)</th>
                        <th>–°—Ç–æ–∏–º–æ—Å—Ç—å (‚Ç∏)</th>
                    </tr>
                    </thead>
                    <tbody>
                    {goods.length === 0 ? (
                        <tr><td colSpan={4}>{loading ? '‚è≥ –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö...' : 'üòî –¢–æ–≤–∞—Ä—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã'}</td></tr>
                    ) : (
                        goods.map((item, i) => (
                            <tr key={item.id} className={i % 2 === 0 ? 'even' : 'odd'}>
                                <td><span className="tag">#{item.id}</span></td>
                                <td>{item.weight.toFixed(1)} –∫–≥</td>
                                <td>{item.pricePerKg.toLocaleString()} ‚Ç∏</td>
                                <td><strong>{item.totalCost.toLocaleString()} ‚Ç∏</strong></td>
                            </tr>
                        ))
                    )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default GoodsApp;
